import headerLogo from '../images/logo.svg';
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import './styles/Login.css';
function Login(props) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  function handleEmailChange(e) {
    setEmail(e.target.value)
  }
  function handlePasswordChange(e) {
    setPassword(e.target.value)
  }
  function handleSubmit(e) {
    e.preventDefault();
    props.onLogin({
      // email: '',
      // password: ''
    });
  }

  return (
    <Login>
      <>
      <header className="header">
        <img className="header__logo" src={headerLogo} alt="Логотип"/>
        <button className="" ><p className="header__text">Войти</p></button>
      </header>
      <div className='auth'>
        <h1 className="auth__title">Войти</h1>
        <section className='auth__form'>
        <div className="auth__container">
          <form onSubmit={handleSubmit} className="auth__form">
            <fieldset className="form__info">
              <input value={email} onChange={handleEmailChange} id="email-input" className=""  type="text" placeholder="Имя" name='name' required minLength="2" maxLength="40"/>
              <span id="name-input-error"></span>
              <input value={password} onChange={handlePasswordChange} id="password-input" className="form__input popup__input form__input_type_about" type="text" placeholder="О себе" name="about" required minLength="2" maxLength="200"/>
              <span id="about-input-error"></span>
              </fieldset>
            <button className="submit-button popup__button submit-button_type_add" type="submit">{`${props.button}`}</button>
          </form>
        </div>
      </section>



        <form onSubmit={handleSubmit} className="auth__form">
          <label htmlFor="username">
            Логин:
          </label>
          <input required id="email" name="email" type="email" value={email} onChange={handleEmailChange} />
          <label htmlFor="password">
            Пароль:
          </label>
          <input required id="password" name="password" type="password" value={password} onChange={handlePasswordChange} />
          <div className="login__button-container">
            <button type="submit" className="login__link">Войти</button>
          </div>
        </form>
      </div>
      </>
    </Login>
  )
}

export default Login;
