import headerLogo from '../images/logo.svg';
import { Link } from 'react-router-dom';


function Header(props) { 
  return (
    <header className="header">
      <img className="header__logo" src={headerLogo} alt="Логотип"/>
      <p className={'header__email ' + (props.loggedIn ? 'header__email_shown' : '')} >{props.email}</p>
      <Link to="/sign-up" className={`header__button `+ (!props.loggedIn && props.registered? " header__button_shown" : "")}>
        <p className="header__text">Войти</p>
      </Link>
      <Link to="/sign-in" className={`header__button `+ (!props.loggedIn && !props.registered ? " header__button_shown" : "")}>
      <p className="header__text">Регистрация</p>
      </Link>
    </header>
  )
}
export default Header;
