import React from "react";
import PopupWithForm from './PopupWithForm.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import useForm from "../hooks/useForm.js";

function EditProfilePopup(props) {
  const { values, handleChange, setValues } = useForm({
    name: '',
    description: ''
  })
  const currentUser = React.useContext(CurrentUserContext);

  React.useEffect(() => {
    setValues([ currentUser.name, currentUser.about ])
    setName(currentUser.name)
    setDescription(currentUser.about);
  }, [currentUser, props.isOpen]); 

  function handleSubmit(e) {
    e.preventDefault();
    props.onUpdateUser({
      name: values.name,
      about: values.description,
    });
  }
  return (
    <PopupWithForm isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit} name='info' title='Редактировать профиль' button="Сохранить" children={
      <>
      <fieldset className="form__info">
        <input value={values.name} onChange={handleChange} id="name-input" className="form__input form__input_type_name"  type="text" placeholder="Имя" name='name' required minLength="2" maxLength="40"/>
        <span id="name-input-error"></span>
        <input value={values.description} onChange={handleChange} id="about-input" className="form__input popup__input form__input_type_about" type="text" placeholder="О себе" name="about" required minLength="2" maxLength="200"/>
        <span id="about-input-error"></span>
      </fieldset>
    </>
    }/>
  )
}
export default EditProfilePopup;
