import React, { useState, useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import InfoTooltip from './InfoTooltip';
import * as auth from '../auth.js'
function Login(props) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  useEffect(() => {
    setEmail('');
    setPassword('');
}, [props.handleLogin]);
  function handleEmailChange(e) {
    setEmail(e.target.value)
  }
  function handlePasswordChange(e) {
    setPassword(e.target.value)
  }
  function handleSubmit(e) {
    e.preventDefault();
    if (!email || !password) {
        props.isAuthPopup();
      }
    auth.authorize(email, password)
    .then((data) => {
      if (data.jwt) {
         () => {
          props.handleLogin(); 
          props.history.push('/')
        }
      }
    })
    .catch(err => console.log(err));
    }
  return (
      <>
      <section className="auth">
        <div className='auth__container'>
          <section className='auth__form'>
            <h1 className="auth__title">Войти</h1>
            <form onSubmit={handleSubmit} className="auth__form">
              <fieldset className="auth__info">
                <input value={email} onChange={handleEmailChange} id="email-input" className="auth__input auth__input_type_email"  type="text" placeholder="Email" name='email' required/>
                <input value={password} onChange={handlePasswordChange} id="password-input" className="auth__input auth__input_type_password" type="password" placeholder="Пароль" name="password" required/>
              </fieldset>
              <button className="auth__submit-button auth__submit-button_type_submit" type="submit">Войти</button>
            </form>
          </section>
        </div>
      </section>
      <InfoTooltip isOpen={props.isOpen} onClose={props.closeAllPopups} name={props.loggedIn ? '' : 'failure'} title={props.loggedIn ? '' : "Что-то пошло не так! Попробуйте ещё раз."}/>
      </>
  )
}

export default withRouter(Login);
