import headerLogo from '../images/logo.svg';
import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import './styles/Login.css';
function Login(props) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  function handleEmailChange(e) {
    setEmail(e.target.value)
  }
  function handlePasswordChange(e) {
    setPassword(e.target.value)
  }
  function handleSubmit(e) {
    e.preventDefault();
    props.onLogin({
      // email: '',
      // password: ''
    });
  }

  return (
    <Login>
      <>
      <header className="header">
        <img className="header__logo" src={headerLogo} alt="Логотип"/>
        <button className="" ><p className="header__text">Войти</p></button>
      </header>
      <div className='auth'>
        <section className='auth__form'>
          <h1 className="auth__title">Войти</h1>
          <form onSubmit={handleSubmit} className="auth__form">
            <fieldset className="auth__info">
              <input value={email} onChange={handleEmailChange} id="email-input" className="auth__input auth__input_type_email"  type="text" placeholder="Email" name='email' />
              <input value={password} onChange={handlePasswordChange} id="password-input" className="auth__input auth__input_type_password" type="text" placeholder="Пароль" name="password" />
            </fieldset>
            <button className="auth__submit-button auth__submit-button_type_login" type="submit">{`${props.button}`}</button>
          </form>
        </section>




        <form onSubmit={handleSubmit} className="auth__form">
          <label htmlFor="username">
            Логин:
          </label>
          <input required id="email" name="email" type="email" value={email} onChange={handleEmailChange} />
          <label htmlFor="password">
            Пароль:
          </label>
          <input required id="password" name="password" type="password" value={password} onChange={handlePasswordChange} />
          <div className="login__button-container">
            <button type="submit" className="login__link">Войти</button>
          </div>
        </form>
      </div>
      </>
    </Login>
  )
}

export default Login;
