      if (typeof body === 'string') {
        opt.body = body; 
      }
      else {
        opt.body = JSON.stringify(body);
      }
    }
    const res = await fetch(this._url + path, opt)
    if(res.ok) {
      return res.json()
    }
    else {
      return Promise.reject(`Ошибка: ${JSON.message}`)
    }
